import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid'
import InfoIcon from '@material-ui/icons/Info';
import PageHeader from "../../components/PageHeader";
import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import CircularProgress from '@material-ui/core/CircularProgress';
import Fade from '@material-ui/core/Fade';
import Controls from "../../components/controls/Controls";
import axios from "axios";
import AddIcon from '@mui/icons-material/Add';
import VisibilityIcon from '@mui/icons-material/Visibility';
import BarChartIcon from '@mui/icons-material/BarChart';
import PlaceIcon from '@material-ui/icons/Place';
import { Link } from 'react-router-dom';
import TableContainer from '@material-ui/core/TableContainer';
import TablePagination from '@material-ui/core/TablePagination';
import { Table, TableHead, TableCell, Paper, TableRow, TableBody, Button, CssBaseline } from '@material-ui/core'
import { useForm, Form } from '../../components/useForm';
import Tooltip from '@mui/material/Tooltip';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import ViewFactors from '../Factors/ViewFactors';
import defaultImg from '../../assets/img/defaultImg.jpeg'
import { ScrollToTop } from '../../components/ScrollToTop'
import { CSVDownloader } from 'react-papaparse'
import DownloadIcon from '@mui/icons-material/Download';
import { getPermissions } from '../../services/userService';

const useStyles = makeStyles(theme => ({
  cardContainer: {
    width: 800,
    justifyContent: "center",
    alignItems: "center"
  },
  media: {
    height: 300,
  },
  table: {
    width: '90%',
    margin: '50px 0 0 0'
  },
  thead: {
    '& > *': {
      fontSize: 20,
      background: '#8ade8f',
      color: '#FFFFFF'
    }
  },
  row: {
    '& > *': {
      fontSize: 18
    }
  },
  buttonheader: {
    display: 'flex'

  },
  placeholder: {
    height: 40,
    textAlign: 'center'
  },
  button: {
    background: '#4287f5',
    color: '#FFFFFF'
  },
  pageContent: {
    width: '90%',
    margin: '50px 0 0 0',
    padding: theme.spacing(3),
  },
  center: {
    display: 'flex',
    textAlign: 'center'
  },
  horizmenu: {
    display: 'inline-block'
  },
  textLeft: {
    marginLeft: '0',
    paddingLeft: '0'
  }
}));

const initialValue = {
  name: '',
  description: '',
  objetives: '',
  justification: '',
  country: '',
  isTimeSeries: true,
  image: '',
  type: '',
  places: [],
  factors: []
}

const initialPlaceValues = {
  name: '',
  description: '',
  latitude: '',
  longitude: '',
  image: '',
  projects: []
}

const cleanPlace = {
  name: '',
  description: '',
  latitude: '',
  longitude: '',
  image: '',
  projects: []
}


export default function ShowProjects() {
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const [toExport, setExport] = useState([]);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };
  const [project, setProject] = useState(initialValue);
  const { name, description, objetives,justification, country, isTimeSeries, image, type, places, factors } = project;
  const [open, setOpen] = React.useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = React.useState(true);
  const [loadingAso, setLoadingAso] = React.useState(false);
  const [placesBio, setPlacesBio] = React.useState([]);
  const [filteredPlaces, setFilteredPlaces] = React.useState([{ name: "" }]);
  const [isPlacesBio, setIsPlacesBio] = React.useState(false);
  const [isEmpty, setIsEmpty] = React.useState(true);
  const [placeImage, setImage] = React.useState("");
  const userType = localStorage.getItem("type");
  const [role, setRole] = useState();
  const classes = useStyles();
  const { id } = useParams();
  useEffect( async() => {

    let unmounted = false;
    setLoading(true);
    if(userType === "user"){
      const response = await getPermissions(localStorage.getItem("uid"), id);
      setRole(response?.data?.role);
    }
    await getPlacesBio();
    await getFilteredPlaces();
    await getProject();
    setLoading(false);
    return () => { unmounted = true; };
  }, []);

  const config = {
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("authToken")}`,
    },
  };

  async function beautifyFactors(){
    const response = await axios.get(
      `http://localhost:5000/api/private/factorproject/${id}`,
      config
    );
    let factorsExport = {};
    const factors = response.data.factors;
    for (const factor in factors){
      delete factors[factor]._id;
      delete factors[factor].__v;
      delete factors[factor].projectID;
      factorsExport[`factor${factor}`] = Object.entries(factors[factor]);
    }
    return factorsExport;
  }

  function beautifyPlaces(){
    let placesExport = {};
    for (const place in placesBio) {
      delete placesBio[place]._id;
      delete placesBio[place].__v;
      delete placesBio[place].projects;
      placesExport[`place${place}`] = Object.entries(placesBio[place]);
    }
    return placesExport;
  }

  async function beautifyCSV(projectP){

    const factors = await beautifyFactors();
    const places = beautifyPlaces();
    let toExport = {
      id: projectP.id,
      name: projectP.name,
      description: projectP.description,
      objetives: projectP.objetives,
      country: projectP.country,
      justification: projectP.justification,
      isTimeSeries: projectP.isTimeSeries,
      type: projectP.type,
    }
    toExport = Object.assign(toExport,places);
    toExport = Object.assign(toExport,factors);
    setExport([toExport]);
  }

  const getProject = async () => {
    try {
      
      let response = await axios.get(`http://localhost:5000/api/private/project/${id}`, config);
      setProject(response.data.project);
      let data = response.data.project;
      
      beautifyCSV(data);
    } catch (error) {
      setTimeout(() => {
        setOpen(false);
        setTimeout(() => {
          setError("");
        }, 2000);

      }, 5000);
      setOpen(true);
      return setError("Authentication failed!");
    }
  }

  const getPlacesBio = async () => {
    try {
      let response = await axios.get(`http://localhost:5000/api/private/placeproject/${id}`, config);
      setPlacesBio(response.data.places);
      setLoading(false);
      if (response.data.places.length > 0)
        setIsPlacesBio(true);
    } catch (error) {
      setTimeout(() => {
        setOpen(false);
        setTimeout(() => {
          setError("");
        }, 2000);

      }, 5000);
      setOpen(true);
      return setError("Authentication failed!");
    }
  }

  const getFilteredPlaces = async () => {
    try {
      let response = await axios.get(`http://localhost:5000/api/private/filteredplace/${id}`, config);
      setFilteredPlaces(response.data.places);
    } catch (error) {
      setTimeout(() => {
        setOpen(false);
        setTimeout(() => {
          setError("");
        }, 2000);

      }, 5000);
      setOpen(true);
      return setError("Authentication failed!");


    }
  }

  const getPicturePlace = async (place) => {
    try {
      if (place) {
        let response = await axios.get(`http://localhost:5000/api/private/placePicture/${place.id}`, config);
        setImage(response.data.place.image);
      } else {
        setImage("");
      }
    } catch (error) {
      console.log(error);
