  }

  const validate = (fieldValues = values) => {
    let temp = { ...errors }
    if ('name' in fieldValues)
      temp.name = fieldValues.name ? "" : "Este campo es obligatorio."
    if ('description' in fieldValues)
      temp.description = fieldValues.description ? "" : "Este campo es obligatorio."
    if ('latitude' in fieldValues)
      temp.latitude = fieldValues.latitude ? "" : "Este campo es obligatorio."
    if ('longitude' in fieldValues)
      temp.longitude = fieldValues.longitude ? "" : "Este campo es obligatorio."
    setErrors({
      ...temp
    })

    if (fieldValues === values)
      return Object.values(temp).every(x => x === "")
  }

  const addNewPlace = async () => {
    try {
      setLoadingAso(true);
      values.projects.push(id);
      return await axios.post(`http://localhost:5000/api/private/place/`, values, config);
    } catch (error) {
      setTimeout(() => {
        setOpen(false);
        setTimeout(() => {
          setError("");
        }, 2000);

      }, 5000);
      setOpen(true);
      setLoadingAso(false);
      return setError("Authentication failed!");
    }
  }

  const updatePlace = async () => {
    try {
      setLoadingAso(true);
      placeValue.projects.push(id);
      return await axios.patch(`http://localhost:5000/api/private/place/${placeValue.id}`, placeValue, config);
    } catch (error) {
      setTimeout(() => {
        setOpen(false);
        setTimeout(() => {
          setError("");
        }, 2000);

      }, 5000);
      setOpen(true);
      setLoadingAso(false);
      return setError("Authentication failed!");
    }
  }

  const updateProject = async (place) => {
    try {
      setLoadingAso(true);
      project.places.push(place._id);
      axios.patch(`http://localhost:5000/api/private/project/${id}`, project, config);
      setLoadingAso(false);
    } catch (error) {
      setTimeout(() => {
        setOpen(false);
        setTimeout(() => {
          setError("");
        }, 2000);

      }, 5000);
      setOpen(true);
      setLoadingAso(false);
      return setError("Authentication failed!");
    }
  }

  const associatePlace = async () => {
    await updatePlace().then(setLoadingAso(false));
    await updateProject(placeValue).then(setLoadingAso(false));
    await getFilteredPlaces();
    await getPlacesBio();
    setPlaceValue(filteredPlaces[0]);
    setInputValue('');
    setIsEmpty(true);
    confirmPost();
  }

  const wrapPlace = async (place) => {
    await updateProject(place).then(setLoadingAso(false));
    await getFilteredPlaces();
    await getPlacesBio();
    confirmPost();
