                          </Tooltip>
                          <Tooltip title="Graficar datos">
                            <Button color="secondary" variant="contained" style={{ marginRight: 10 }} component={Link} to={`/graphics/${id}/${place._id}`}>
                              <BarChartIcon />
                            </Button>
                          </Tooltip>
                          <Tooltip title="Mostrar datos">
                            <Button color="inherit" variant="contained" style={{ marginRight: 10 }} component={Link} to={`/data/show/${id}/${place._id}`}>
                              <VisibilityIcon />
                            </Button>
                          </Tooltip>
                          <Tooltip title="Agregar datos">
                            <Button color="primary" variant="contained" style={{ marginRight: 10 }} component={Link} to={`/data/add/${id}/${place._id}`}>
                              <AddIcon />
                            </Button>
                          </Tooltip>

                        </Grid>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[10, 25, 100]}
              component="div"
              count={placesBio.length}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
            />
          </Paper>
        </div>
        <div className={classes.placeholder} hidden={isPlacesBio}>
          <br />
          <Typography variant="subtitle1" color="textSecondary" component="p">
            No hay nada qu茅 mostrar en est谩 secci贸n
          </Typography>
        </div>
      </div>
      <br id="asociar" />
      <br />
      <PageHeader
        title="Asociar lugar a proyecto"
        subTitle="Seleccione un lugar para asociarlo a este proyecto"
        icon={<InfoIcon fontSize="large"
        />}
      />
      <div className={classes.placeholder} hidden={!loadingAso}>
        <Fade
          in={loadingAso}
          style={{
            transitionDelay: '0m',
          }}
          unmountOnExit
        >
          <CircularProgress />
        </Fade>
        <br />
      </div>
      <Paper className={classes.pageContent}>
        <Grid
          container
          direction="row"
          justifyContent="center"
          alignItems="center"
        >
          <Autocomplete
            value={placeValue}
            onChange={(event, newValue) => {
              getPicturePlace(newValue);
              setPlaceValue(newValue);

            }}
            className={classes.center}
            id="combo-box-places"
            options={filteredPlaces}
            getOptionLabel={(option) => option.name}
            style={{ width: 300 }}
            renderInput={(params) => <TextField {...params} label="Lugares" variant="outlined" />}
            disabled={loadingAso}
            inputValue={inputValue}
            onInputChange={(event, newInputValue) => {
              setInputValue(newInputValue);
              if (newInputValue === '') {
                setIsEmpty(true);
                setPlaceValue('');
              }
              else
                setIsEmpty(false);
            }}
          />

        </Grid>
        {/* <div hidden={!isEmpty}>

          <Form onSubmit={handleSubmit}>
            <br />
            <Divider />
