                style={{marginTop: '20px'}}
              >

                <Controls.Button
                  type="submit"
                  text="AGREGAR Y ASOCIAR"
                />
              </Grid>
            </Grid>
          </Form>
        </div> */}

        <div hidden={isEmpty}>
          <br />
          <Grid
            container
            direction="row"
            justifyContent="center"
            alignItems="center"
          >
            <Card className={classes.cardContainer}>

              <CardMedia
                className={classes.media}
                image={placeImage ? placeImage : defaultImg}
                title=""
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  {placeValue ? placeValue.name : ''}
                </Typography>
                <Typography variant="subtitle1" color="textSecondary" component="p">
                  {placeValue ? placeValue.description : ''}
                </Typography>
              </CardContent>

              <CardActions>
                <Typography variant="subtitle1" color="primary" component="p">
                  <span >Latitud: </span>
                  {placeValue ? placeValue.latitude : ''}
                </Typography>
                <Typography variant="subtitle1" color="primary" component="p">
                  <span>Longitud: </span>
                  {placeValue ? placeValue.longitude : ''}
                </Typography>
              </CardActions>
            </Card>
          </Grid>
          <br />
          <Grid
            container
            direction="row"
            justifyContent="center"
            alignItems="center"
          >
            <Controls.Button
              text="Asociar lugar"
              color="primary"
              onClick={associatePlace} />
          </Grid>
        </div>
      </Paper>

      <br />
      <br />
      <div className={classes.placeholder} hidden={!loadingAso}>
        <Fade
          in={loadingAso}
          style={{
            transitionDelay: '0m',
          }}
          unmountOnExit
        >
          <CircularProgress />
