import axios from 'axios';

const config = {
    headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("authToken")}`,
    }
};
const programURL = 'http://localhost:5000/api/private/program';


export const getPrograms = async (id) => {
    try {
        id = id || '';
        return await axios.get(`${programURL}/${id}`, config);
    } catch (error) {
        return new TypeError("Authentication failed!");
    }
}

export const addProgram = async (program) => {
    try {
        return await axios.post(`${programURL}/`, program, config);
    } catch (error) {
        return new TypeError("Authentication failed!");
    }
}

export const deleteProgram = async (id) => {
    try {
        return await axios.delete(`http://localhost:5000/api/private/program/${id}`, config);
    } catch (error) {
        throw Error(error?.response?.data?.error);
    }
}

export const editProgram = async (id, program) => {
    try {
        return await axios.patch(`${programURL}/${id}`, program, config)
    } catch (error) {
        return new TypeError("Authentication failed!");
    }
}
